import React, { useState, useEffect } from "react";
import { Nav, Image } from "react-bootstrap";
import Logout from "./Logout";
import User from "../container/UserDetails";
import ImgDetails from "../container/ImgDetails";

function Header() {
  const [userDetails, setUserDetails] = useState(null);
  const [img, setImg] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const data = await User();
        setUserDetails(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchImgData = async () => {
      try {
        const data = await ImgDetails({ postId: userDetails.id, type: "avt" });
        setImg(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    fetchImgData();
  }, []);

  let result;
  if (img) {
    result = (
      <Card.Img
        variant="top"
        src={"http://localhost:3000/images/" + img.imgName}
      />
    );
  }

  return (
    <Nav className="d-flex justify-content-between">
      <Nav.Item>
        <Nav.Link href="/home">Social Network</Nav.Link>
      </Nav.Item>

      <Nav.Item className="d-flex justify-content-between">
        <Nav.Item>
          <Nav.Link href="/info">
            {userDetails && <p>{userDetails.fullName}</p>}
          </Nav.Link>
        </Nav.Item>
        <Image
          src="https://genk.mediacdn.vn/2018/10/19/photo-1-15399266837281100315834-15399271585711710441111.png"
          alt="User Avatar"
          roundedCircle
          style={{ width: "40px", height: "40px" }}
        />
        <Logout></Logout>
      </Nav.Item>
    </Nav>
  );
}

export default Header;
