import Card from "react-bootstrap/Card";
import { faHeart, faComment } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import User from "../container/UserDetails";
const PostCard = (postId) => {

    const [userDetails, setUserDetails] = useState(null);

    useEffect(() => {
      const fetchUserData = async () => {
        try {
          const data = await User();
          setUserDetails(data);
        } catch (error) {
          console.error("Error fetching user data:", error);
        }
      };
  
      fetchUserData();
    }, []); // Chỉ chạy một lần khi component mount
  


  return (
    <div>
      <Card.Text style={{ marginLeft: "20px" }}>Sờ ta tút</Card.Text>
      <div>
        <FontAwesomeIcon icon={faHeart} />
        <span style={{ fontSize: "13px" }}>17 Likes</span>
        <FontAwesomeIcon style={{ marginLeft: "10px" }} icon={faComment} />
      </div>
      <Card.Title style={{ fontSize: "16px" }}>
        <img
          src="https://genk.mediacdn.vn/2018/10/19/photo-1-15399266837281100315834-15399271585711710441111.png"
          alt="Avatar"
          style={{
            maxWidth: "50px",
            maxHeight: "50px",
            borderRadius: "50%",
          }}
        />
        Nguyễn Đăng Phòng
      </Card.Title>
    </div>
  );
};

export default PostCard;
