import Stack from "react-bootstrap/Stack";
import Card from "react-bootstrap/Card";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPaperPlane } from "@fortawesome/free-solid-svg-icons";
import { Form, InputGroup, Button } from "react-bootstrap";
import Comment from "./Comment";
import PostCard from "./PostCard";
import User from "../container/UserDetails";
import React, { useState, useEffect } from "react";

const Post = ({ postId, userId, content }) => {
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const data = await User(userId);
        console.log(""+data);
        setUserDetails(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const data = await User();
        setUserDetails(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="p-2 d-flex justify-content-center align-items-center">
      <Card style={{ maxWidth: "500px" }}>
        <Card.Img
          variant="top"
          src="https://genk.mediacdn.vn/2018/10/19/photo-1-15399266837281100315834-15399271585711710441111.png"
        />
        <Card.Body>
          <Stack gap={3}>
            <PostCard postId={postId} userFullname={userDetails?.fullName} />
            <div>
              <Comment
                avatarSrc="https://i1.sndcdn.com/artworks-000623435371-0tmohq-t500x500.jpg"
                username="Nguyễn Hữu Đa"
                comment="Phải tôi tôi đấm cho mấy nhát"
              />
            </div>
          </Stack>
          <div>
            <Form style={{ marginTop: "20px" }}>
              <InputGroup className="mb-3">
                <Form.Control type="email" placeholder="Viết bình luận..." />
                <Button variant="primary">
                  <FontAwesomeIcon icon={faPaperPlane} />
                </Button>
              </InputGroup>
            </Form>
          </div>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Post;
