import Stack from "react-bootstrap/Stack";
import Post from "./Post";
import ValidateToken from "../common/ValidateToken";
import { React, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import HomeDetails from "../container/HomeDetails";

const Home = () => {
  const navigate = useNavigate();
  const [homeDeatails, sethomeDeatails] = useState(null);

  useEffect(() => {
    const isValidToken = ValidateToken();
    if (!isValidToken) {
      navigate("/login");
    }
  }, []);

  useEffect(() => {
    const fetchHomeDetailsData = async () => {
      try {
        const data = await HomeDetails();
        console.log("API Response:", data);
        sethomeDeatails(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    fetchHomeDetailsData();
  }, []);

  return (
    <Stack gap={homeDetails.content.length}>
      <Post postId={1} />
      <p>{homeDeatails.content.length}</p>
    </Stack>
  );
};

export default Home;
