import Stack from "react-bootstrap/Stack";
import Post from "./Post/Post";
import ValidateToken from "../common/ValidateToken";
import { React, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import HomeDetails from "../container/HomeDetails";
import { Form } from "react-bootstrap";

const Home = () => {
  const navigate = useNavigate();
  const [homeDetails, setHomeDetails] = useState(null);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const isValidToken = ValidateToken();
    if (!isValidToken) {
      navigate("/login");
    }
  }, []);

  useEffect(() => {
    const fetchHomeDetailsData = async () => {
      try {
        const data = await HomeDetails();
        setHomeDetails(data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchHomeDetailsData();
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <Form>
        <Form.Group
          className="mb-3"
          style={{ marginBottom: "0px" }}
          controlId="exampleForm.ControlTextarea1"
        >
          <Form.Control placeholder="Bạn đang nghĩ gì ?" as="textarea" rows={2} />
        </Form.Group>
        <Form.Group controlId="formFile" className="mb-3">
          <Form.Control type="file" />
        </Form.Group>
        
        <Button variant="primary">
                  <FontAwesomeIcon icon={faPaperPlane} />
                </Button>
      </Form>
      <Stack gap={homeDetails?.content?.length || 0}>
        {homeDetails?.content?.map((post) => (
          <Post key={post.id} post={post} />
        ))}
      </Stack>
    </div>
  );
};

export default Home;
