import Stack from "react-bootstrap/Stack";
import Card from "react-bootstrap/Card";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faHeart,
  faComment,
  faPaperPlane,
} from "@fortawesome/free-solid-svg-icons";
// Home.js
import React from "react";
import { Form, InputGroup, Button } from "react-bootstrap";
import Comment from "./Comment";

const Home = () => {
  return (
    <Stack gap={3}>
      <div className="p-2 d-flex justify-content-center align-items-center">
        <Card style={{ maxWidth: "500px" }}>
          <Card.Img
            variant="top"
            src="https://genk.mediacdn.vn/2018/10/19/photo-1-15399266837281100315834-15399271585711710441111.png"
          />
          <Card.Body>
            <Card.Title style={{ fontSize: "16px" }}>
              <img
                src="https://genk.mediacdn.vn/2018/10/19/photo-1-15399266837281100315834-15399271585711710441111.png"
                alt="Avatar"
                style={{ maxWidth: "40px", borderRadius: "50%" }}
              />
              Nguyễn Đăng Phòng
            </Card.Title>
            <Card.Text style={{ marginLeft: "20px" }}>Sờ ta tút</Card.Text>
            <div>
              <FontAwesomeIcon icon={faHeart} />
              <span style={{ fontSize: "13px" }}>17 Likes</span>
              <FontAwesomeIcon
                style={{ marginLeft: "10px" }}
                icon={faComment}
              />
            </div>
            <Stack gap={3}>
              <div>
                <Comment
                  avatarSrc="https://i1.sndcdn.com/artworks-000623435371-0tmohq-t500x500.jpg"
                  username="Nguyễn Hữu Đa"
                  comment={42}
                />
              </div>
            </Stack>
            <div>
              <Form style={{ marginTop: "20px" }}>
                <InputGroup className="mb-3">
                  <Form.Control type="email" placeholder="Viết bình luận..." />
                  <Button variant="primary">
                    <FontAwesomeIcon icon={faPaperPlane} />
                  </Button>
                </InputGroup>
              </Form>
            </div>
          </Card.Body>
        </Card>
      </div>
    </Stack>
  );
};

export default Home;
